import requests as rqimport pandas as pdimport io as ioimport timedef getYahooData(symbol,start_date,end_date,interval):        posixTimeStart = str(int(time.mktime(start_date.timetuple())))    posixTimeEnd = str(int(time.mktime(end_date.timetuple())))    queryURL = 'https://query1.finance.yahoo.com/v7/finance/download/'\        + symbol +'?'\        + 'period1=' + posixTimeStart +'&'\        + 'period2=' + posixTimeEnd + '&'\        + 'interval=' +'1d'\        + '&events=history'            http = rq.get(queryURL)    dataString = io.StringIO(http.content.decode(http.encoding))    df = pd.read_csv(dataString,sep=",")    df["Date"] = pd.to_datetime(df["Date"])    df = df.set_index("Date")    return df